targets.query:
from(bucket:"data-realtime")
|>range(start: -10m, stop: now())
|>filter(fn:(r) => r._measurement == "sunspec_power_meter")
|>filter(fn:(r) => r._field == "M_AC_Power_A")
|>map(fn: (r) => ({
    r with
    _value:
      if r._value <= 0 then 0
      else int(v: r._value)
}))

from(bucket:"data-realtime")
|>range(start: -10m, stop: now())
|>filter(fn:(r) => r._measurement == "sunspec_power_meter")
|>filter(fn:(r) => r._field == "M_AC_Power_A")
|>map(fn: (r) => ({
    r with
    _value:
      if r._value >= 0 then 0
      else int(v: r._value * -1.0)
}))

from(bucket:"data-realtime")
|>range(start: v.timeRangeStart, stop: v.timeRangeStop)
|>filter(fn:(r) => r._field == "M_AC_Voltage_AN_computed")
|>map(fn: (r) => ({
    r with
    _value: (r._value / 100.0)
}))
