var now = new Date().getTime();

var scaleFactorFields = {
    "I_AC_Current": "I_AC_Current_SF",
    "I_AC_CurrentA": "I_AC_Current_SF",
    "I_AC_CurrentB": "I_AC_Current_SF",
    "I_AC_CurrentC": "I_AC_Current_SF",
    "I_AC_VoltageAB": "I_AC_Voltage_SF",
    "I_AC_VoltageAN": "I_AC_Voltage_SF",
    "I_AC_VoltageBN": "I_AC_Voltage_SF",
    "I_AC_VoltageCN": "I_AC_Voltage_SF",
    "I_AC_Power": "I_AC_Power_SF",
    "I_AC_Frequency": "I_AC_Frequency_SF",
    "I_DC_Current": "I_DC_Current_SF",
    "I_DC_Voltage": "I_DC_Voltage_SF",
    "I_DC_Power": "I_DC_Power_SF",
    "I_Temp_Sink": "I_Temp_SF",
    "I_AC_Energy_WH": "I_AC_Energy_WH_SF",
    "M_AC_Current": "M_AC_Current_SF",
    "M_AC_Current_A": "M_AC_Current_SF",
    "M_AC_Current_B": "M_AC_Current_SF",
    "M_AC_Current_C": "M_AC_Current_SF",
    "M_AC_Voltage_LL": "M_AC_Voltage_SF",
    "M_AC_Voltage_AB": "M_AC_Voltage_SF",
    "M_AC_Voltage_BC": "M_AC_Voltage_SF",
    "M_AC_Voltage_CA": "M_AC_Voltage_SF",
    "M_AC_Voltage_LN": "M_AC_Voltage_SF",
    "M_AC_Voltage_AN": "M_AC_Voltage_SF",
    "M_AC_Voltage_BN": "M_AC_Voltage_SF",
    "M_AC_Voltage_CN": "M_AC_Voltage_SF",
    "M_AC_Freq": "M_AC_Freq_SF",
    "M_AC_Power": "M_AC_Power_SF",
//    "M_AC_Power_A": "M_AC_Power_SF",
    "M_AC_Power_B": "M_AC_Power_SF",
    "M_AC_Power_C": "M_AC_Power_SF",
    "M_AC_VA": "M_AC_VA_SF",
    "M_AC_VA_A": "M_AC_VA_SF",
    "M_AC_VA_B": "M_AC_VA_SF",
    "M_AC_VA_C": "M_AC_VA_SF",
    "M_AC_VAR": "M_AC_VAR_SF",
    "M_AC_VAR_A": "M_AC_VAR_SF",
    "M_AC_VAR_B": "M_AC_VAR_SF",
    "M_AC_VAR_C": "M_AC_VAR_SF",
    "M_AC_PF": "M_AC_PF_SF",
    "M_AC_PF_A": "M_AC_PF_SF",
    "M_AC_PF_B": "M_AC_PF_SF",
    "M_AC_PF_C": "M_AC_PF_SF",
/*    "M_Exported": "M_Energy_W_SF",
    "M_Exported_A": "M_Energy_W_SF",
    "M_Exported_B": "M_Energy_W_SF",
    "M_Exported_C": "M_Energy_W_SF",
    "M_Imported": "M_Energy_W_SF",
    "M_Imported_A": "M_Energy_W_SF",
    "M_Imported_B": "M_Energy_W_SF",
    "M_Imported_C": "M_Energy_W_SF",*/
    "M_Imported_VA": "M_Energy_VA_SF",
    "M_Exported_VA": "M_Energy_VA_SF",
    "M_Import_VARh_Q1": "M_Energy_VAR_SF",
    "M_Import_VARh_Q2": "M_Energy_VAR_SF",
    "M_Export_VARh_Q3": "M_Energy_VAR_SF",
    "M_Export_VARh_Q4": "M_Energy_VAR_SF"
};

var inverterFields = {};
var powerMeterFields = {};
var hasMeterData = false;

Object.keys(msg.payload).forEach(function(key, index) {
    var value = Number(msg.payload[key]);

    if (scaleFactorFields[key]) {
        var scalingFactor = Number(msg.payload[scaleFactorFields[key]]);
        value = parseFloat((value * Math.pow(10, scalingFactor)).toFixed(12));
        key = key + "_computed";
    }

    if (!Number.isNaN(value) && Number.isFinite(value)) {
        if (key.startsWith("M_")) {
            powerMeterFields[key] = value;
            hasMeterData = true;
        } else if (key.startsWith("I_")) {
            inverterFields[key] = value;
        }
    }
});

if (inverterFields["I_DC_Power_computed"] > 0) {
    var acPowerOutput = msg.payload.I_AC_Power * Math.pow(10, msg.payload.I_AC_Power_SF);
    var dcPowerInput = msg.payload.I_DC_Power * Math.pow(10, msg.payload.I_DC_Power_SF);
	inverterFields["I_efficiency_computed"] = parseFloat(acPowerOutput / dcPowerInput * 100).toFixed(12);
}

msg.payload = [
    {
        "measurement": "sunspec_inverter",
        "fields": inverterFields,
        "timestamp": now
    }
];

if (hasMeterData) {
    msg.payload.push({
        "measurement": "sunspec_power_meter",
        "fields": powerMeterFields,
        "timestamp": now
    });
}

return msg;

var timestamp = new Date(msg.payload.period_end).getTime();

msg.payload = [
    {
        "measurement": "solcast_forecast",
        "fields": {
            "pv_estimate": parseFloat(msg.payload.pv_estimate * 1000).toFixed(2),
            "pv_estimate10": parseFloat(msg.payload.pv_estimate10 * 1000).toFixed(2),
            "pv_estimate90": parseFloat(msg.payload.pv_estimate90 * 1000).toFixed(2)
        },
        "timestamp": timestamp
    }
];

return msg;

var timestamp = new Date(msg.payload.period_end).getTime();

msg.payload = [
    {
        "measurement": "solcast_actual",
        "fields": {
            "pv_estimate": parseFloat(msg.payload.pv_estimate * 1000).toFixed(2)
        },
        "timestamp": timestamp
    }
];

return msg;

var queryDate = new Date();
msg.topic = '';

for (var i = 0; i < 25; i++) {

    var rangeStartDate = new Date();
    rangeStartDate.setHours(queryDate.getHours() - i - 1, 0, 0, 0);
    var rangeStart30Date = new Date();
    rangeStart30Date.setHours(queryDate.getHours() - i - 1, 30, 0, 0);
    var rangeStopDate = new Date();
    rangeStopDate.setHours(queryDate.getHours() - i, 0, 0, 0);

    if (msg.topic) {
        msg.topic = msg.topic + '\n';
    }

    msg.topic = msg.topic + `
from(bucket: "data-historical")
  |> range(start: ` + rangeStartDate.toISOString() + ', stop: ' + rangeStart30Date.toISOString() + `)
  |> filter(fn: (r) => r["_measurement"] == "sunspec_inverter")
  |> filter(fn: (r) => r["_field"] == "I_AC_Power_computed")
  |> mean()
  |> map(fn: (r) => ({
    r with
    _value: float(v: r._value) / 1000.0
  }))
  |> yield(name: "` + i + `-1")
` + '\n' + `
from(bucket: "data-historical")
  |> range(start: ` + rangeStart30Date.toISOString() + ', stop: ' + rangeStopDate.toISOString() + `)
  |> filter(fn: (r) => r["_measurement"] == "sunspec_inverter")
  |> filter(fn: (r) => r["_field"] == "I_AC_Power_computed")
  |> mean()
  |> map(fn: (r) => ({
    r with
    _value: float(v: r._value) / 1000.0
  }))
  |> yield(name: "` + i + `-2")
`;

}

msg.period = "PT30M";

return msg;

var solcastMeasurements = [];

if (msg.payload && msg.payload.length > 0) {
    for (var i = 0; i < msg.payload.length; i++) {
        if (msg.payload[i] && msg.payload[i].length > 0) {
            for (var j = 0; j < msg.payload[i].length; j++) {
                if (msg.payload[i][j] && msg.payload[i][j]["_value"] !== null) {
                    solcastMeasurements.push({
                        "period_end": msg.payload[i][j]["_stop"],
                        "period": msg.period,
                        "total_power": msg.payload[i][j]["_value"]
                    });
                }
            }
        }
    }
}

msg.payload = {
    "measurements": solcastMeasurements
};

return msg;
